import { Divider, Grid, List, ListItem, ListItemIcon, ListItemText, Typography } from "@material-ui/core"
import {index} from '../../../state/AppAction'
import {useDispatch,useSelector} from 'react-redux'
import React,{useEffect} from 'react'
import { API_URL } from "../../../../constants/constants"
import { FETCH_BUSES } from "../../../state/Constants"
import BusesLoading from "../../../loading/BusesLoading"
import { DirectionsBus } from "@material-ui/icons"
import Reat,{useState} from 'react'
const BusList = ()=>{
    const dispatch = useDispatch()
    const buses = useSelector(state=>state.authReducer.busesReducer.buses.data);
    const loading = useSelector(state=>state.authReducer.busesReducer.loading)
    const [bus,setBus] = useState(null)
    
    useEffect(()=>{
        dispatch(index(`${API_URL}buses`,FETCH_BUSES))
    })

    const handleClick =bus=>{
        setBus(bus)
    }
    return(
            <div>
                {
                    loading
                    ?
                        (
                          <BusesLoading/>  
                        )
                    :
                        (
                            <Grid container spacing={2}>
                                <Grid item md={3}>
                                    <List>
                                        {
                                            buses.map(bus=>(
                                                <ListItem button item md={12} xs={12}>
                                                    <ListItemIcon>
                                                        <DirectionsBus/>
                                                    </ListItemIcon>
                                                    <ListItemText
                                                     primary={bus.plate_number}
                                                     secondary={bus.number}
                                                     onClick={()=>handleClick(bus)}
                                                    />
                                                </ListItem>
                                            ))
                                        }
                                    </List>
                                </Grid>

                                <Grid item md={1}>
                                    <Divider
                                     orientation={'vertical'}
                                     flexItem
                                    />
                                </Grid>
                                <Grid item md={8}>
                                    <div className={{display:'flex',flexDirection:'row',justifyContent:'flex-start'}}>
                                        <Divider
                                        orientation={'vertical'}
                                        flexItem/>

                                        {
                                            bus===null
                                            ?
                                                (
                                                    <Typography>
                                                        Select on your bus
                                                    </Typography>
                                                )
                                            :
                                                (
                                                    <Typography>
                                                        {bus.number}
                                                    </Typography>
                                                )
                                        }
                                    </div>
                                </Grid>
                            </Grid>
                        )
                }
            </div>
        )
    }

export default BusList
