import { Typography } from '@material-ui/core'
import { useState } from 'react'
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator'
import LoadingButton from '../commons/LoadingButton'
import useStyles from '../styles/homeStyle'
import UseForm from '../commons/UseForm'
export const Login =()=>{
    const classes = useStyles() 
    const {inputs, handleInputChange, handleSubmit} = UseForm({phone:'',password:''});   
    return (
            <ValidatorForm className={classes.loginContainer} onSubmit={handleSubmit} >
                    {
                        <Typography component='p'>
                        </Typography>
                        }
                    <TextValidator
                        className={classes.text_input}
                        label={'Phone'}
                        onChange={handleInputChange}
                        name="phone"
                        type='phone'
                        value={inputs.phone}
                        validators={['required']}
                        errorMessages={['Please add your phone number']}
                    />

                    <TextValidator
                    className={classes.text_input}
                    label={'Password'}
                    name="password"
                    type='password'
                    onChange={handleInputChange}
                    value={inputs.password}
                    validators={['required']}
                    errorMessages={['Please enter your password']}
                    />               
                    <LoadingButton
                            className={classes.text_input}
                            color="primary"
                            variant="contained"
                            type="submit"
                            text={'Login'}
                        >
                            {
                                'Login'
                            }
                    </LoadingButton>
            </ValidatorForm>
    )
}

export default Login
