import {makeStyles, maleStyles} from '@material-ui/core'

const BusStyle = makeStyles((theme)=>({
    container:{
        display:'flex',
        flexDirection:'row',
        justifyContent:"center"
    },
    form:{
        display:'flex',
        flexDirection:'column',
        width:'80%'
    },
    text_input:{
        width:'100%',
        marginBottom:20
    },
    driverContainer:{
        display:'flex',
        flexDirection:'row',
        justifyContent:'space-between'
    },
    noDriverContainer:{
        display:'flex',
        flexDirection:'column',
    }
}))

export default BusStyle
