import { AppBar, CssBaseline, Hidden, IconButton, Menu, SwipeableDrawer, Divider,Toolbar, Typography, Container, Avatar } from '@material-ui/core'
import {Route } from 'react-router-dom'
import {Switch} from 'react-router-dom'
import authStyle from '../auth/style/auth_style'
import DrawerProfile from './DrawerProfile'
import theme from '../themes/AppTheme'
import { useState } from 'react'
import AdminDrawerMenu from './AdminDrawerMenu'
import AdminNestedRoute from '../auth/Admin/routes/AdminNestedRoute'
import {getRole} from '../TokenService'
const CommonDashboard = (props)=>{
    const classes = authStyle()
    const [mobileOpen,setMobileOpen] = useState(false)
    const [currentPage,setCurrentPage] = useState("Dashboard")
    const {container} = props;
    const drawer = (
        <div className={classes.drawerRoot}>
            <DrawerProfile/>
            <AdminDrawerMenu menu={props.drawerMenu}/>
        </div>
    )

    const routesComponent =()=>{
        const roleId = JSON.parse(getRole()).id
        if(roleId===2){
            return <AdminNestedRoute/>
        }
    }

   const handleDrawerToggle = (value,page) => event=>{
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }
        setMobileOpen(value)
        setCurrentPage(value)
    }
    return(
        <div className={classes.root}>
        <CssBaseline/>
        <div style={{display:'flex',flexDirection:'column'}}>
            <AppBar position="fixed" className={classes.appBar} elevation={0}>
                <Toolbar>
                    <IconButton
                        color="primary"
                        aria-label="open drawer"
                        edge="start"
                        onClick={handleDrawerToggle(true)}
                        className={classes.menuButton}
                    >
                        <Menu/>
                    </IconButton>
                    <div className={classes.grow}/>
                    <Avatar variant={'circular'} style={{width:30,height:30,marginRight:25}}>
                        A
                    </Avatar>
                </Toolbar>
                <Divider/>
            </AppBar>
        </div>
        <nav className={classes.drawer}>
            {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
            <Hidden smUp implementation="css">
                <SwipeableDrawer
                    container={container}
                    variant="temporary"
                    anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                    open={mobileOpen}

                    onOpen={handleDrawerToggle(true,currentPage)}
                    onClose={handleDrawerToggle(false,currentPage)}
                    classes={{
                        paper: classes.drawerPaper,
                    }}
                    ModalProps={{
                        keepMounted: true, // Better open performance on mobile.
                    }}
                >
                    {drawer}
                </SwipeableDrawer>
            </Hidden>
            <Hidden xsDown implementation="css">
                <SwipeableDrawer
                    classes={{
                        paper: classes.drawerPaper,
                    }}
                    variant="permanent"
                    open={mobileOpen}
                    onOpen={handleDrawerToggle(true,currentPage)}
                    onClose={handleDrawerToggle(false,currentPage)}
                >
                    {drawer}
                </SwipeableDrawer>
            </Hidden>
        </nav>
        <main className={classes.content}>
            <div className={classes.toolbar}/>
            <Container maxWidth={'lg'}>
                {routesComponent}
            </Container>
        </main>
    </div>
    )
}

export default CommonDashboard
