import { IconButton, Menu, MenuItem, Typography } from "@material-ui/core"
import { Language } from  "@material-ui/icons"
import useStyles from '../styles/homeStyle'
import React from 'react'
const LanguageChanger = ()=>{
    const classes = useStyles()
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
      };
    
      const handleClose = () => {
        setAnchorEl(null);
      };

    return(
        <div className={classes.toolbarLanguage}>
            <Typography color={'inherit'}>
                Lanugages
            </Typography>
            <IconButton size={'medium'} color={'inherit'} aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                <Language/>
            </IconButton>
            <Menu
                 id="simple-menu"
                 anchorEl={anchorEl}
                 keepMounted
                 open={Boolean(anchorEl)}
                 onClose={handleClose}
               >
                 <MenuItem value={1} onClick={handleClose}>English</MenuItem>
                 <MenuItem onClick={handleClose}>Amharic</MenuItem>
                 <MenuItem onClick={handleClose}>Oromiffa</MenuItem>
                 <MenuItem onClick={handleClose}>Tigrigna</MenuItem>
               </Menu>
        </div>
    )
}

export default LanguageChanger
