import {FETCH_BUSES} from '../Constants'

const initialState = {
    buses:{
        data:[]
    },
    loading:true
}

export default function(state=initialState,action){
    switch(action.type){
        case FETCH_BUSES:
            return{
                ...state,
                loading:false,
                buses:action.payload

            }

            default:
                return state
    }
}
