import axios from 'axios'

const index =(path,actionType)=>dispatch=>{
    axios.get(path)
    .then(response=>response.data)
    .then(res=>dispatch({
        type:actionType,
        payload:res
    }))
}

const store = (path,data,actionType)=>dispatch=>{
    axios.post(path,data)
    .then(response=>response.data)
    .then(res=>dispatch({
        type:actionType,
        payload:res
    }))
}

const update = (path,data,actionType)=>dispatch=>{
    axios.put(path,data)
    .then(response=>response.data)
    .then(res=>dispatch({
        type:actionType,
        payload:res
    }))
}

export const show =(path,actionType)=>dispatch=>{
    axios.get(path)
    .then(response=>response.data)
    .then(res=>dispatch({
        type:actionType,
        payload:res
    }))
}
