import {makeStyles} from '@material-ui/core'

const bankStyle = makeStyles((theme)=>(
    {
        accounts:{
            display:'flex',
            flexDirection:'column',
            alignItems:'center'
        },
        no_account:{
            display:'flex',flexDirection:'column',alignItems:'center'
        }
    }
))

export default bankStyle
