import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator'
import formStyle from '../../../../style/formStyle'
import UseForm from '../../../../../commons/UseForm'
import { Button, Grid } from '@material-ui/core'
const AddNewCashier =()=>{
    const classes = formStyle()
    const {inputs,handleInputChange} = UseForm({'name':'','phone':'',password:''})
    return(
        <div className={classes.container}>
            <ValidatorForm className={classes.form}>
                
                <TextValidator
                    className={classes.text_input}
                    label={'Cashier name'}
                    onChange={handleInputChange}
                    name="name"
                    type='text'
                    value={inputs.name}
                    validators={['required']}
                    errorMessages={['Please enter cashier name']}
                />

                <TextValidator
                    className={classes.text_input}
                    label={'Cashier phone number'}
                    onChange={handleInputChange}
                    name="phone"
                    type='text'
                    value={inputs.phone}
                    validators={['required']}
                    errorMessages={['Please enter cashier phone number']}
                />

                <Grid container spacing={2}>
                    <Grid item md={7} xs={12} sm={12}>
                        <TextValidator
                        className={classes.text_input}
                        label={'Cashier phone number'}
                        onChange={handleInputChange}
                        name="phone"
                        type='text'
                        value={inputs.phone}
                        validators={['required']}
                        errorMessages={['Please enter cashier phone number']}/>
                    </Grid>

                    <Grid item md={5} xs={12} sm={12}>
                        <Button variant={'outlined'} color={'secondary'} style={{textTransform:'none',marginTop:20}}>
                            Generate password
                        </Button>
                    </Grid>
                </Grid>

            </ValidatorForm>
        </div>
    )
}

export default AddNewCashier
