import { Typography } from '@material-ui/core'
import { useState } from 'react'
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator'
import LoadingButton from '../commons/LoadingButton'
import useStyles from '../styles/homeStyle'
import UseForm from '../commons/UseForm'
import {useTranslation} from 'react-i18next'
export const Login =()=>{
    const classes = useStyles() 
    const {inputs, handleInputChange} = UseForm({phone:'',password:''});
    const {t} = useTranslation() 
  const  handleSubmit = ()=>{
        console.log(inputs)
    }  
    return (
            <ValidatorForm className={classes.loginContainer} onSubmit={handleSubmit} >
                    {
                        <Typography component='p'>
                        </Typography>
                        }
                    <TextValidator
                        className={classes.text_input}
                        label={t('home.phone')}
                        onChange={handleInputChange}
                        name="phone"
                        type='phone'
                        value={inputs.phone}
                        validators={['required']}
                        errorMessages={['Please add your phone number']}
                    />

                    <TextValidator
                    className={classes.text_input}
                    label={t('home.password')}
                    name="password"
                    type='password'
                    onChange={handleInputChange}
                    value={inputs.password}
                    validators={['required']}
                    errorMessages={['Please enter your password']}
                    />               
                    <LoadingButton
                            className={classes.text_input}
                            color="primary"
                            variant="contained"
                            type="submit"
                            text={t('home.login')}
                        >
                            {
                                t('home.login')
                            }
                    </LoadingButton>
            </ValidatorForm>
    )
}

export default Login
