import {FETCH_BANKS} from '../Constants'

const initialState = {
    banks:[],
    loading:true
}

export default function(state=initialState,action){
    switch(action.type){
        case FETCH_BANKS:
            return{
                ...state,
                loading:false,
                banks:action.payload
            }

            default:
                return state
    }
}
