import { Typography } from '@material-ui/core'
import { useState } from 'react'
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator'
import LoadingButton from '../commons/LoadingButton'
import useStyles from '../styles/homeStyle'
import UseForm from '../commons/UseForm'
import {useTranslation} from 'react-i18next'
import axios from 'axios'
import {useHistory} from 'react-router-dom'

import { API_AUTH_URL } from '../constants/constants'
export const Login =()=>{
    const classes = useStyles() 
    const {inputs, handleInputChange} = UseForm({phone:'',password:''});
    const {t} = useTranslation() 
    const history  =useHistory()


   const  handleSubmit = ()=>{
        axios.post(`${API_AUTH_URL}login`,inputs)
        .then(response=>response.data)
        .then(res=>{
            set(res.token)
            history.push('/auth')
         })
         .catch(onerror=>{
            setLoading(false)
            setFinished(false)
            setSubmitted(false)
    
            if(!onerror.response){
                setErrorMessage('Something is not good. Please check your internet connection ):')
            }else {
                let code = onerror.response.status
                if(code===403){
                    setErrorMessage('Incorrect phone or password is used')
                }
            }
         })
    }  
    return (
            <ValidatorForm className={classes.loginContainer} onSubmit={handleSubmit} >
                    {
                        <Typography component='p'>
                        </Typography>
                        }
                    <TextValidator
                        className={classes.text_input}
                        label={t('home.phone')}
                        onChange={handleInputChange}
                        name="phone"
                        type='phone'
                        value={inputs.phone}
                        validators={['required']}
                        errorMessages={[t('home.error.phone')]}
                    />

                    <TextValidator
                    className={classes.text_input}
                    label={t('home.password')}
                    name="password"
                    type='password'
                    onChange={handleInputChange}
                    value={inputs.password}
                    validators={['required']}
                    errorMessages={[t('home.error.password')]}
                    />               
                    <LoadingButton
                            className={classes.text_input}
                            color="primary"
                            variant="contained"
                            type="submit"
                            text={t('home.login')}
                        >
                            {
                                t('home.login')
                            }
                    </LoadingButton>
            </ValidatorForm>
    )
}

export default Login
